import React from "react";
import Accordion from "react-bootstrap/Accordion";
import Card from "react-bootstrap/Card"; import { BadgeCheckIcon, ChipIcon } from "@heroicons/react/solid";

import "bootstrap/dist/css/bootstrap.min.css";
import { skills2 } from "../data";

export default function App() {
    
    return (
        <section id="skills2">
            <Accordion>

                {skills2.map((skill, index) => (
                    <div key={skill} className="p-2 sm:w-1/2 w-full">
                        <Card>
                            <Card.Header>
                                <Accordion.Toggle as={Card.Header} variant="link" eventKey={jsindex.toString}>
                                    {skill.title}
                                    {index}
                                </Accordion.Toggle>
                            </Card.Header>
                            <Accordion.Collapse eventKey={index.toString}>
                                <Card.Body>
                                    {skill.list.map((skill) => (
                                        <div key={skill} className="p-2 sm:w-1/2 w-full">
                                            <div className="bg-gray-800 rounded flex p-4 h-full items-center">
                                                <BadgeCheckIcon className="text-green-400 w-6 h-6 flex-shrink-0 mr-4" />
                                                <span className="title-font font-medium text-white">
                                                    {skill}
                                                </span>
                                            </div>
                                        </div>
                                    ))}
                                </Card.Body>
                            </Accordion.Collapse>
                        </Card>
                    </div>
                ))}

                {/* 
                <Card>
                    <Card.Header>
                        <Accordion.Toggle as={Card.Header} variant="link" eventKey="0">
                            title 1
                        </Accordion.Toggle>
                    </Card.Header>
                    <Accordion.Collapse eventKey="0">
                        <Card.Body>body 1</Card.Body>
                    </Accordion.Collapse>
                </Card>
                <Card>
                    <Card.Header>
                        <Accordion.Toggle as={Card.Header} variant="link" eventKey="1">
                            title 2
                        </Accordion.Toggle>
                    </Card.Header>
                    <Accordion.Collapse eventKey="1">
                        <Card.Body>body 2</Card.Body>
                    </Accordion.Collapse>
                </Card> */}
            </Accordion>
        </section>
    );
}
